mapper.select:
SELECT
    file_num
FROM
    fileupload
WHERE
    section = #{section}
    AND common_num = #{common_num}
    AND identy = #{identy}

SELECT
    original_file_name
FROM
    fileupload
WHERE
    section = #{section}
    AND common_num = #{common_num}
    AND identy = #{identy}

SELECT
    ROUND(file_size/1024,1) AS file_size
FROM
    fileupload
WHERE
    section = #{section}
    AND common_num = #{common_num}
    AND identy = #{identy}
