mapper.select:
select * from reply
	where board_num = #{board_num}
	order by board_num desc


 select 
   reply_num, board_num, reply_content, reply_date<!-- , replyer -->
 from
   (
   select /*+INDEX_DESC(reply pk_reply))*/
     rownum rn, reply_num, board_num, reply_content, reply_date<!-- , replyer -->
   from reply
   where board_num = #{board_num}
   and reply_num > 0
   and rownum <= #{cri.page} * #{cri.perPageNum}
   )
where rn > ( #{cri.page} -1) * #{cri.perPageNum}
